sysauto_script.script:
//Number of NeedIt records to create
var recordsToMake = 15;

// Declare and initialize variables used in the script
var genNIRec;
var i;
var tableName = "x_820118_stds_student_details_list";
var physicsArray = ["45", "65", "78"];
var chemistryArray = ["87", "78", "98"];
var mathematicsArray = ["39", "75", "78"];
var computerScienceArray = ["49", "99", "100"];
var selectPhysics = "";

var studentNameArray = [{
    "name": "Amith",
    "email": "amith@example.com"
}, {
    "name": "Abhishek",
    "email": "abhishek@example.com"
}, {
    "name": "Pandu",
    "email": "pandu@example.com"
}];
var pickUser = 10;

for (i = 0; i < recordsToMake; i++) {
    genNIRec = new GlideRecord(tableName);
    genNIRec.newRecord();


    //Select a random value from the requestType array and set the Request Type field value (choice)
    selectPhysics = physicsArray[(Math.floor(Math.random() * 3))];
    genNIRec.setValue("physics", selectPhysics);

	selectChemistry = chemistryArray[(Math.floor(Math.random() * 3))];
    genNIRec.setValue("chemistry", selectChemistry);

	selectMathematics = mathematicsArray[(Math.floor(Math.random() * 3))];
    genNIRec.setValue("mathematics", selectMathematics);

	selectComputerScience = physicsArray[(Math.floor(Math.random() * 3))];
    genNIRec.setValue("computer_science", selectComputerScience);

    genNIRec.setValue("short_description", "Automatically created NeedIt request: " + selectPhysics + ", " + selectChemistry + " ," + selectMathematics + "," + selectComputerScience);

    pickUser = (Math.floor(Math.random() * 3));
    genNIRec.student_name.setDisplayValue(studentNameArray[pickUser].name);
    genNIRec.setValue("student_email", studentNameArray[pickUser].email.toString());



    genNIRec.insert();
}
